    server_sock_fd  = socket(AF_INET,SOCK_STREAM,0);
    memset(&server_addr,0,sizeof(server_addr));
    server_addr.sin_family = AF_INET;
    server_addr.sin_port = htons(8888);
    server_addr.sin_addr.s_addr = inet_addr("127.0.0.1");
    int on = 1;
    setsockopt(server_sock_fd,SOL_SOCKET,SO_REUSEADDR,(char*)&on,sizeof(on));
    if(bind(server_sock_fd,(struct sockaddr*)&server_addr,sizeof(server_addr)) == -1)
    {
        std::cout << "bind error\n" << std::endl;
        return -1;
    }

    if(listen(server_sock_fd,10) == -1)
    {
        std::cout << "listen error\n" << std::endl;
        return -1;
    }

    addr_size = sizeof(client_addr);
    client_sock_fd = accept(server_sock_fd,(struct sockaddr*)&client_sock_fd,&addr_size);
    if(addr_size == -1)
    {
        std::cout << "accept error\n";
        return -1;
    }

    sleep(10);
    write(client_sock_fd,message,sizeof(message));
    close(client_sock_fd);
    close(server_sock_fd);
    return 0;
